import React, { Component } from 'react';
class MainComponent extends Component {
    state = { 
        code:["A","B","C",1,2,3],
        codeSoFar:"",
        listOfCode:[]

     }
     handleClick(co){
         let code=this.state.codeSoFar;
         this.setState({codeSoFar:code+co})  
     }
     handleAddCode=()=>{
         let codeis=this.state.codeSoFar
         if(this.state.listOfCode.includes(codeis)){
            this.setState({codeSoFar:""})
             alert("Code allready  exist")
         }else{
            this.state.listOfCode.push(codeis)
            this.setState({codeSoFar:""})
         } 
     }
     handleClear=()=>{
         this.setState({codeSoFar:""})
     }
    render() { 
        return ( 
            <div className="container">
                <h1>Create New Code</h1>
                <h3>Code So Far : {this.state.codeSoFar}</h3>
                <div className="row">
                <div key={"A"} onClick={()=>this.handleClick("A")} className="col-1  m-2 bg-warning">A</div>
                <div key={"B"} onClick={()=>this.handleClick("B")} className="col-1  m-2 bg-warning">B</div>
                <div key={"c"} onClick={()=>this.handleClick("C")} className="col-1  m-2 bg-warning">C</div>
                <div key={1} onClick={()=>this.handleClick(1)} className="col-1  m-2 bg-warning">1</div>
                <div key={2} onClick={()=>this.handleClick(2)} className="col-1  m-2 bg-warning">2</div>
                <div key={3} onClick={()=>this.handleClick(3)} className="col-1  m-2 bg-warning">3</div></div>
                <div className="row">
                    <button onClick={()=>this.handleAddCode()} className="btn btn-primary m-2">Add New Code</button>
                    <button onClick={()=>this.handleClear()} className="btn btn-primary m-2">Clear Code</button>
                </div>
                <h1>List of Code Created</h1>
                {this.state.listOfCode.map(loc=>(<ul key={loc}>
                    <li>{loc}</li>
                </ul>))}

            </div>
         );
    }
}

export default MainComponent;
