import React, { Component } from 'react';
class MainComponent extends Component {
    state = { 
        code:["A","B","C",1,2,3],
        codeSoFae:"",
        liseOfCode:[]

     }
     handleClick(code){
         console.log(code)
     }
    render() { 
        return ( 
            <div className="container">
                <h1>Create New Code</h1>
                <h3>Code So Far :</h3>
                <div className="row">
                <div onClick={()=>this.handleClick("A")} className="col-1  m-2 bg-warning">A</div>
                <div onClick={()=>this.handleClick("B")} className="col-1  m-2 bg-warning">B</div>
                <div onClick={()=>this.handleClick("C")} className="col-1  m-2 bg-warning">C</div>
                <div onClick={()=>this.handleClick(1)} className="col-1  m-2 bg-warning">1</div>
                <div onClick={()=>this.handleClick(2)} className="col-1  m-2 bg-warning">2</div>
                <div onClick={()=>this.handleClick(3)} className="col-1  m-2 bg-warning">3</div></div>
                <div className="row">
                    <button className="btn btn-primary m-2">Add New Code</button>
                    <button className="btn btn-primary m-2">Clear Code</button>
                </div>
                <h2>List of Codes Created</h2>
                {this.state.liseOfCode.map(lc=>(<ul>
                    <li>{lc}</li>
                </ul>))}

            </div>
         );
    }
}

export default MainComponent;
